import React from 'react';

function Result(props) {
    let media = undefined;

    switch (props.type) {
        default:
        case 'image':
            media = <img src={props.src} alt={"An image relating to " + props.searchedTerm} onClick={props.toggleInterest}/>;
            break;
        case 'video':
            media = <video src={props.src} alt={"A video relating to " + props.searchedTerm}/>
            break;
    }

    return (
        <div>
            {media}
            <input type='button' value={props.interest ? "\u2700" : "X"} onSubmit={(event) => {props.setInterest(!props.interest); event.preventDefault()}}/>
        </div>
    );
}

class ResultsPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {results: this.props.results.slice()};

        this.getSetInterest = (index) => {
            return (interest) => {
                const results = this.state.results.slice();
                results[index] = Object.assign({}, results[index]);
                results[index].interest = interest;
                this.setState({results: results});
                console.log("Setting interest of " + results[index] + " to " + interest);
            }
        }

        this.goBack = this.goBack.bind(this);
    }

    goBack() {
        this.props.setInterests(this.props.searchedTerm, this.state.results.filter(result => result.interest).map(result => result.src));
        this.props.goBack();
    }

    render() {
        
        return (
            <div className="ResultsPage">
                {this.state.results.map((result, index) => 
                    <Result
                        key={result.src}
                        searchedTerm={this.props.searchedTerm}
                        src={result.src}
                        type={result.type}
                        interest={result.interest}
                        setInterest={this.getSetInterest(index)}
                    />
                )}
                <input type='button' value='Go Back' onClick={this.goBack}/>
            </div>
        );
    }
}

export default ResultsPage;
