function Result(props) {
    let media = undefined;

    const handleClick = event => {
        if (props.interest) {
            props.removeInterest(props.src);
        } else {
            props.showInterest(props.src);
        }
    }

    const handleCheckboxChange = event => {
        if (props.interest) { // event.target.checked also an option
            props.removeInterest(props.src);
        } else {
            props.showInterest(props.src);
        }
    }

    switch (props.type) {
        default:
        case 'image':
            media = <img src={props.src} alt={"An image relating to " + props.searchedTerm} onClick={handleClick}/>;
            break;
        case 'video':
            media = <video src={props.src} alt={"A video relating to " + props.searchedTerm}/>
            break;
    }

    return (
        <div className="Result">
            {media}
            <input 
                type='checkbox'
                onChange={handleCheckboxChange}
                checked={props.interest}
            />
        </div>
    );
}

function ResultsPage(props) {
    return (
        <ul className='ResultsGrid'>
            {props.results.map(result =>
                <li key={result.src}>
                    <Result
                        searchedTerm={props.searchedTerm}
                        src={result.src}
                        type={result.type}
                        interest={result.interest}
                        showInterest={props.showInterest}
                        removeInterest={props.removeInterest}
                    />
                </li>
            )}
        </ul>
    );
}

export default ResultsPage;
