import React from 'react';

function Result(props) {
    let media = null;

    switch (props.type) {
        default:
            console.error("Unkown result type: " + props.type);
            break;
        case 'image':
            media = <img className="ResultMedia" src={props.src} alt={"An image relating to " + props.searchedTerm} onClick={() => props.setInterest(!props.interest)} />;
            break;
        case 'video':
            //media = <div className="ResultMedia" dangerouslySetInnerHTML={props.player}/>;
            break;
    }

    return (
        <div className="Result">
            <div>
                {media}
            </div>
            <input
                type='button'
                value={props.interest ? "Interested!" : "Not Interested"} // checkmark :)
                onClick={(event) => {props.setInterest(!props.interest); event.preventDefault()}}
                style={props.interest ? {color:"green"} : {color:"red"}}/>
        </div>
    );
}

class ResultsPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {results: this.props.results.slice()};
        this.state.results.sort(() => Math.random() - 5); // sorts randomly

        this.getSetInterest = (index) => {
            return (interest) => {
                const results = this.state.results.slice();
                results[index] = Object.assign({}, results[index]);
                results[index].interest = interest;
                this.setState({results: results});
                console.log("Setting interest of " + results[index].src + " to " + interest);
            }
        }

        this.goBack = this.goBack.bind(this);
    }

    goBack() {
        this.props.setInterests(this.props.searchedTerm, this.state.results.filter(result => result.interest).map(result => result.src));
        this.props.goBack();
    }

    render() {
        if (this.state.results.length < this.props.results.length) {
            // props results was updated
            // need to copy over the extra results we dont have
            // and then concat them to the state results
            // and then setState
            // we also shuffle them
            const statesResultsIncludes = (result) => {
                return this.state.results.reduce(function(acc, elem) {
                    if (!acc) {
                        if (elem.src === result.src) {
                            acc = true;
                        }
                    }
                    return acc;
                }, false);
            }

            const newResults = this.props.results.filter((result) => !statesResultsIncludes(result));
            newResults = newResults.concat(this.state.results);
            newResults.sort(() => Math.random() - 5);
            this.setState({results: newResults});
        }
        
        return (
            <div className="ResultsPage">
                <div>
                    {this.state.results.map((result, index) => 
                        <Result
                            key={index}
                            searchedTerm={this.props.searchedTerm}
                            src={result.src}
                            type={result.type}
                            interest={result.interest}
                            setInterest={this.getSetInterest(index)}
                        />
                    )}
                </div>
                <input className='GoBackButton' type='button' value='Go Back' onClick={this.goBack}/>
            </div>
        );
    }
}

export default ResultsPage;
