import React from 'react';

function Result(props) {
    let media = undefined;
    switch (props.type) {
        default:
        case 'image':
            media = <img src={props.src} alt={"An image relating to " + props.searchedTerm}/>;
            break;
        case 'video':
            media = <video src={props.src} alt={"A video relating to " + props.searchedTerm}/>
            break;
    }

    return (
        <div className="Result">
            {media}
            <input 
                type='checkbox'
                onChange={function(event) {
                    if (event.target.checked) {
                        props.showInterest(props.src);
                    } else {
                        props.removeInterest(props.src);
                    }

                    event.preventDefault();
                }}
                checked={props.interest}
            />
        </div>
    );
}

function ResultsPage(props) {
    return (
        <ul className='ResultsGrid'>
            {props.results.map(result => <Result
                searchedTerm={this.props.searchedTerm}
                src={result.src}
                type={result.type}
                interest={result.interest}
                showInterest={this.props.showInterest}
                removeInterest={this.props.removeInterest}
            />)}
        </ul>
    );
}

export default ResultsPage;
