import './App.css';
import React from 'react';
import SearchPage from './SearchPage.js'
import ResultsPage from './ResultsPage.js'

const GOOGLE_API_KEY = 'AIzaSyAM0zBXsd42sz2F_EcHnQMfr09CJ0pirlY' // would make this an env var in a bigger project
const GOOGLE_CX = 'c55eb86d669bd3886' // would make this an env var in a bigger project

class App extends React.Component {
  constructor(props) {
    super(props);

    // interests is an object with key=searched term and
    // value=links that the user said they were interested in
    this.state = {curPage: 'search', interests: {}, searchedTerm: ""};

    this.searchForTerm = this.searchForTerm.bind(this);
    this.showInterest = this.showInterest.bind(this);
    this.removeInterest = this.removeInterest.bind(this);
  }

  searchForTerm(term) {
    // results properties:
    // src: link to the image/video
    // type: "image" or "video"
    // interest: bool
    console.log("Searching for " + term);
    // search for term
    // for some reason the google API uses http2
    // and that is not usable by an app created by create-react-app,
    // so i will handle the http request directly (sad)
    const req = new XMLHttpRequest();
    // search for 7 images relating to term on cx, a custom search engine
    req.open("GET", "https://customsearch.googleapis.com/customsearch/v1?key=" + GOOGLE_API_KEY +
        "&cx=" + GOOGLE_CX + "&q=" + term + "&num=7&searchType=IMAGE");

    req.onreadystatechange = () => {
      if (req.readyState === XMLHttpRequest.DONE) {
        const json = JSON.parse(req.responseText);
        console.log("Image results: " + json);

        if (json["error"] === undefined) {
          const results = json.items.map(result => { return {src: result.link, type: "image"} });

          const linksWithInterest = this.state.interests[term];
          if (linksWithInterest !== undefined) {
            results.forEach(result => result.interest = linksWithInterest.includes(result.src));
          }
          this.setState({curPage: 'results', results: results, searchedTerm: term});
        } else {
          console.error("unable to search for " + term);
          console.error(req.responseText);
        }
      }
    }

    req.send();
  }

  // showIntererst
  // adds the link to the interests object in an array with key term
  // return true iff state updated
  // does not affect this.state.results
  showInterest(link) {
    console.log('adding interest of ' + link);
    let newInterests = undefined;

    if (this.state.interests[this.state.searchedTerm] === undefined) {
      newInterests = Object.assign({}, this.state.interests); // copy interests
      newInterests[this.state.searchedTerm] = [link];

    } else if (!this.state.interests[this.state.searchedTerm].includes(link)) {
      newInterests = Object.assign({}, this.state.interests); // copy interests
      newInterests[this.state.searchedTerm].push(link);

    } else {
      return false;
    }

    this.setState({interests: newInterests});
    return true;
  }

  // removes the link from the interests obj, if it exists
  // return true iff the object was updated
  // does not affect this.state.results
  removeInterest(link) {
    console.log('removing interest of ' + link);
    const inters = this.state.interests;

    if (inters[this.state.searchedTerm] !== undefined) {

      const index = inters[this.state.searchedTerm].indexOf(link);
      if (index !== -1) {
        const newInterests = Object.assign({}, inters);
        const newArr = newInterests[this.state.searchedTerm].slice(); // copy the array
        newArr.splice(index, 1);                                      // delete the link from the array
        if (newArr.length === 0) {
          delete newInterests[this.state.searchedTerm];               // remove the term from the interests obj if the array is empty
        } else {
          newInterests[this.state.searchedTerm] = newArr;
        }

        this.setState({interests: newInterests,});
        return true;
      }
    }
    return false;
  }

  render() {
    switch(this.state.curPage) {
      default:
        console.log('Unknown page ' + this.state.curPage);
        this.setState({curPage: 'search'});
        // falls through
      case 'search':
        return (
          <div>
            <SearchPage searchForTerm={this.searchForTerm}/>
            <input className="InterestsButton" type='button' value='Get Interests (JSON)' onClick={event => this.setState({curPage: 'interests'})}/>
          </div>
        );
      case 'results':
        return (
          <div>
            <ResultsPage
              searchedTerm={this.state.searchedTerm}
              results={this.state.results}
              showInterest={this.showInterest}
              removeInterest={this.removeInterest}
              goBack={() => this.setState({curPage: 'search'})}
            />
          </div>
        );
      case 'interests':
        return (
          <div>
            <div>
              <code>{JSON.stringify(this.state.interests)}</code>
            </div>
            <input className="GoBackButton" type='button' value='Go Back' onClick={() => this.setState({curPage: 'search'})}/>
          </div>
        );
    }
  }
}

export default App;
