import React from 'react';

class SearchPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {term: ""};

        this.handleChange = event => {
            this.setState({term: event.target.value});
            event.preventDefault();
        }

        this.onSubmit = event => {
            if (this.state.term.length > 0) {
                this.props.searchForTerm(this.state.term);
            }
            event.preventDefault(); // prevent refresh of the page
        }
    }

    render() {
        return (
            <form onSubmit={this.onSubmit} className="SearchPage">
                <center><table style={{width:"60%", textAlign: "center"}}><tr>
                    <td style={{width:"85%"}}><input
                        className="SearchBar" 
                        type='text'
                        value={this.state.term}
                        placeholder='Search for something!'
                        onChange={this.handleChange}
                    /></td>
                    <td style={{textAlign:"left", width:"15%"}}><input style={{paddingLeft:"15px"}} className="SearchButton" type='submit' value='Search!'/></td>
                </tr></table></center>
            </form>
        );
    }
}

export default SearchPage;
