import React from 'react';

class SearchPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {term: ""};

        this.handleChange = event => {
            this.setState({term: event.target.value});
            event.preventDefault();
        }

        this.onSubmit = event => {
            if (this.state.term.length > 0) {
                console.log("Searching for " + this.state.term);
                this.props.searchForTerm(this.state.term);
            }
            console.log(this.state.term);
            event.preventDefault(); // prevent refresh of the page
        }
    }

    render() {
        return (
            <form onSubmit={this.onSubmit}>
                <input type='text' value={this.state.term} placeholder='Search for something!' onChange={this.handleChange}/>
                <input type='submit' value='Search!'/>
            </form>
        );
    }
}

export default SearchPage;
