import React from 'react';

class SearchPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {term: ""};

        this.handleChange = event => {
            this.setState({term: event.target.value});
            event.preventDefault();
        }
    }

    render() {
        return (
            <form onSubmit={(event) => {this.props.searchForTerm(this.state.term); event.preventDefault()}}>
                <input type='text' value={this.state.term} placeholder='Search for something!' onChange={this.handleChange}/>
                <input type='button' value='Search!'/>
            </form>
        );
    }
}

export default SearchPage;
