function Result(props) {
    let media = undefined;

    switch (props.type) {
        default:
        case 'image':
            media = <img src={props.src} alt={"An image relating to " + props.searchedTerm} onClick={props.toggleInterest}/>;
            break;
        case 'video':
            media = <video src={props.src} alt={"A video relating to " + props.searchedTerm}/>
            break;
    }

    return (
        <div className="Result">
            {media}
            <input type='checkbox'
                onChange={props.toggleInterest}
                checked={props.interest}
            />
        </div>
    );
}

class ResultsPage extends React.Component {
    constructor(props) {
        // copy over the results prop into state so we can easily change interest
        this.state = {results: this.props.results.slice()};
        this.goBack = (event) => {
            // show/remove interest for all results
            // this can be optimized a little further but it is probably
            // not worth it for this small of a project
            this.state.results.forEach(element => {
                if (element.interest) {
                    this.props.showInterest(element.src);
                } else {
                    this.props.removeInterest(element.src);
                }
            });

            this.props.goBack(); // goes back to the other page
            event.preventDefault();
        }

        this.getToggleInterest = (i, interest) => {
            return () => {
                const results = this.state.results.slice();
                results[i] = Object.assign({}, results[i]);
                results[i].interest = !interest;
                this.setState({results: results});
            }
        }
    }

    render() {
        return (
            <div>
                <div className='ResultsGrid'>
                    {this.state.results.map((result, i) =>
                        <div key={result.src}>
                            <Result
                                searchedTerm={this.props.searchedTerm}
                                src={result.src}
                                type={result.type}
                                interest={result.interest}
                                toggleInterest={this.getSetInterest(i, result.interest)}
                            />
                        </div>
                    )}
                </div>
                <div>
                    <input className="GoBackButton" type='button' value='Go Back' onClick={this.goBack}/>
                </div>
            </div>
        );
    }
}

export default ResultsPage;
